  clearCache({ state }, view) {
    const cache = state.keepAliveRef.cache;
    const keys = state.keepAliveRef.keys;
    let index = -1
    for (let i = 0; i < keys.length; i++) {
      if (keys[i] && keys[i].endsWith(view.fullPath)) {
        index = i
        break
      }
